    <FileUpload
          accept=".jpg,.png,.jpeg"
          label="Upload Second Image"
          multiple
          updateFilesCb={updateUploadedFiles}
        />
    </div>

      </div>
      <form onSubmit={handleSubmit}>
      <div style = {{textAlign:"center"}}>

      <Button  type="primary" onClick={compareImages}>
              Upload and compare images
            </Button>

      </div>

      </form>
    </div>
  );
}

export default App;
